var random_vowel = function(){
  var vowels = ['a', 'e', 'i', 'o', 'u'];
  return vowels[Math.floor(Math.random()*vowels.length)];
};

var is_vowel = function(e){
  var vowels = ['a', 'e', 'i', 'o', 'u', 'y'];
  var is = vowels.indexOf(e) != -1 ? true : false;
  return is;
};

var sanctify = {
  baptist: function(un_holy_speach){
    var holy_speach = un_holy_speach.split(' ').map(function(word){
      return word.split('').map(function(e){
        if (e.match(/[^a-zA-Z]/g)) return '';
        if (is_vowel(e)) {
          return e;
        } else {
          return e + random_vowel();
        }
      }).reverse().join('').toLowerCase();
    }).filter(function(word){
      return word.length > 1;
    }).reverse().join(' ');

    return holy_speach;
  }
};

module.exports = sanctify;
