var expect = require('chai').expect,
    sanctify = require('../index'),
    baptist = sanctify.baptist;

describe('#baptist', function() {
  it('returns a string', function() {
    expect(baptist('landover baptist')).to.be.a('string');
  });
  it('returns longer string than input', function() {
    var str = 'landover baptist';
    expect(baptist(str).length).to.be.above(str.length);
  });
  it('returns lowercase only', function() {
    var str = baptist('UPPER CASE');
    expect(str).to.equal(str.toLowerCase());
  });
  it('only returns characters', function() {
    var s = baptist('string with ,.;2358701+_?><>!@#$%^&*');
    expect(s).to.be.a('string')
    expect(s.match(/[^a-z ]/g)).to.be.a('null');
  });
});

